#pragma once	// include this header				// only once per source file#include "GlobalDefines.h"#include "Document.h"#include "Windows.h"#include <Printing.h>typedef struct {	OSType		Filetype;							// bekannter Filetype	OSErr		(*DoLoad)(FSSpec *f,WORD count,WORD whatToDo);	// Laderoutine für diesen Typ} AutoLoadStruct;typedef struct {	void		(*DoAbout)(void);			// About wurde angewählt	void		(*DoMenu)(short title,short entry);	// Menüpunkt wurde angewählt	void		(*AdjustMenu)(Boolean*);	// Menüpunkte enablen bzw. disablen	void		(*InitMenus)(void);			// hier. Menüs initialisieren	void		(*InitDoc)(DocHandle d);	// Dokumentenstruktur anlegen	void		(*WindInit)(WindPtr w);		// Grafport des neuen Windows initialisieren	void		(*Exit)(void);				// Exit-Funktion => Programmende	void		(*SuspendResume)(Boolean);	// Wird aufgerufen, wenn App in Hintergrund tritt	void		(*Idle)(void);				// globaler Idle-Event#if GERD	void		(*WAktion)(Rect *);			// wird in Window-Aktion aufgerufen#endif	void		(*WElement)(REG WindPtr wind,Point p,REG short thePart);	// zusätzliches Window-Element angeklickt//	void		(*Scroll)(ControlHandle wind,short part,long *cur);	// zusätzliches Window-Element angeklickt	void		(*Print)(TPPrPort port,THPrint p);		// wird in Window-Aktion aufgerufen	void		(*PrintDlg)(short type,DialogPtr d,short item); // Dialoge für Drucken	void		(*Key)(LONG key,Boolean *cont);			// Taste wurde gedrückt	WORD		SleepTime;					// maximale Zeit zwischen zwei Null-Events	WORD		FileTypesCount;	SFTypeList	FileTypes;					// für maximal 4 Typen Platz	WORD		AutoLoadCount;				// Anzahl der folgenden Filetypes	AutoLoadStruct	AutoLoad[4];			// Filetypes, die mit Doppelklick geladen werden können} GlobalStruct;extern	GlobalStruct	gG;					// Globale Funktionen#define	G(funk) if (gG.funk) (*gG.funk)		// globale Funktion aufrufen