#pragma once	// include this header				// only once per source file#include "Windows.h"#define MAXCLASSES	30				// maximale Anzahl der Klassen#define WINDEXTRA	6				// Menüpunkte zwischen den Windows und den Dokumenten#define MIN_WINDOW_X	120			// minimale Windowbreite#define MIN_WINDOW_Y	120			// minimale Windowhöhetypedef struct {	WORD		moffset;	// Menü-Offset der Klasse	Boolean		type;		// true => nur ein Window pro Klasse	UCHAR		count;		// Anzahl der offenen Fenster der Klasse	Str31		name;		// Name des Menüeintrags der Klasse} Class,*ClassPtr,**ClassH;EXTERN	WORD		gClassAttribut;		// Stil der WindowklassenEXTERN	ClassPtr	gC;					// Klassen-Array[Menü-Offset]EXTERN	MenuHandle	gWindowMenu;		// Handle vom Window-MenüEXTERN	WORD		gWMenuOffset;		// maximale Anzahl der Windowtypen + 1WORD	GetClass(WORD moffset);WORD	MenuId(REG WORD class,WindPtr w);WORD	IsPNameInMenu(Str255 title);VOID	AppendWindowMenu(WORD id);VOID	WMenuUpdate(Boolean *redrawMenuBar);VOID	TileWindows(VOID);VOID	CleanUpWindows(VOID);Boolean	SelectIt(VOID);VOID	DoWindow(REG WindPtr wind,REG WORD item);Boolean	SelectAll(VOID);