/*** *	Library für die serielle Schnittstelle ***/#pragma once	// include this header				// only once per source file#include <Serial.h>#define	SERIALBUF	2048L		// Größe des Eingangsbuffers#define	XOn			0x11		// Software-Handshake-Codes#define	XOff		0x13#define	SDInitPara	baud9600+data8+stop10+noParityextern	Str63		SDName;			// Name des aktiven Portsextern	WORD		SDIn,SDOut;		// RefNum für Lesen und Schreibenextern	WORD		SDPort;			// aktueller Ausgabeportextern	USTR		SDInBuf;		// Buffer für den Eingangextern	WORD		SDBreakChar;	// Break-Char (-1 = keins)extern	UWORD		SDChecksum;		// Prüfsumme für Lesen VOM Organizerextern	UWORD		SDTimeout;		// Timeoutlänge beim Empfang in 60HzVOID	SDLookup(Boolean (*callback)(WORD index,USTR portname,USTR indriver,USTR outdriver));OSErr	SDClose(VOID);OSErr	SDOpen(VOID);OSErr	SDWait(REG UWORD timeout,REG LONG *count);OSErr	SDWrite(USTR s,UWORD count);OSErr	SDWriteCmd(UWORD cmd);OSErr	SDRead(USTR buf,UWORD maxbytes);VOID	SDSetBrk(WORD brk);