/*** *	eigene Windowelemente ***/#include "Windows.h"#define	USEWINDELEMENT	0		// True, wenn WindElement benutzt wird#define	wItemHit		128		// Hit-Offset auf das erste Item#define	wItemDraw		128		// Item zeichnen#define	wItemSet		129		// Item an Liste anhŠngen#define	wItemRect		130		// Itemrechteck errechnen#define CallWind(w,message,param)	(*(pascal long (*)(short,WindPtr,short,long))StripAddress(*((WindowPeek)(w))->windowDefProc))(0,(w),(message),(param))#define	SelectWindItem(w,param)		CallWind(w,wItemDraw,0x10000 | (param))#define DeselectWindItem(w,param)	CallWind(w,wItemDraw,(param))#define AddWindItem(w,id1,id2)		CallWind(w,wItemSet,((long)(id1) << 16) | (id2))Boolean		TrackWindItem(WindPtr wind,Point p,short thePart);